--Size
include std/io.e
sequence
	-- We'll reuse instruction later, but here it's just a temp variable
	instruction = command_line(),
	
	-- read_lines() comes from io.e, and is an easy way to read in an entire text file.
	-- Each line is stripped of new line characters.
	code = read_lines( instruction[3] )  - '0',
	
	-- These are initialized using Hex Strings.  It's fewer tokens than using repeat()
	ram = -1 & x"00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 ",
	reg = x"00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 "

integer pc = 0

-- don't bother with checking for running off the end...
-- if we don't get a 100 we'll crash, but that's per spec!
while 1 do
	pc += 1
	instruction = code[pc]
	
	-- Grab these up here and use them later.
	-- Making them integers prevents type checks and dereference attempts later
	integer 
		r1 = instruction[2]+1,
		v1 = reg[r1],
		opnd2 = instruction[3] -- this could crash on a line with just ?x

	if opnd2 < 0 then
		-- this op is special...only 2 elements. and
		-- we assume opnd2 is the result of ' ' - '0', which is less than zero
		? v1
	else
		integer 
			op = instruction[1]
		if op > 4 or not op then
			-- ops 0, 5,6,.. need a register value, not a literal value
			-- doing this here allows us to combine literal/register ops below
			opnd2 = reg[opnd2+1]
		end if
		-- For a super fast switch, all of the cases should be integers, and 
		-- the difference between max and min should be 1024 or less.  This
		-- allows the interpreter to use a precomputed jump table combined with
		-- a type check (make sure you passed an integer), bounds check and a 
		-- subtraction.
		switch op do
			-- these first few do their thing and continue at the top of the loop
			case 0  then
				if opnd2 then
					pc = v1
				end if
				continue
			case 1  then exit
			case 9  then ram[opnd2+1] = v1       continue
			case 15 then writef( "[1] ", opnd2 ) continue
			
			-- now the ops that assign to a register
			-- ...they all exit the switch normally and do the actual assignment
			case 8    then v1  = ram[opnd2+1]
			case 2, 5 then v1  = opnd2
			case 3, 6 then v1 += opnd2
			case      else v1 *= opnd2 -- ops 4, 7
		end switch
		reg[r1] = v1
	end if
	
end while
